package victor.miner.stratum.out;

public class SubmitMessage extends OutMessage {

	private static final String MSG_TEMPLATE = "{\"id\":%d,\"method\":\"mining.submit\",\"params\":[\"%s\",\"%s\",\"%s\",\"%s\",\"%s\"]}";	
	private String username, jobId, nTime, nonceRight, solution;
	
	public SubmitMessage(int id, String username, String jobId, String nTime, String nonceRight, String solution) {
		this.setId(id);
		this.username = username;
		this.jobId = jobId;
		this.nTime = nTime;
		this.nonceRight = nonceRight;
		this.solution = solution;
	}
	
	@Override
	public String toString() {
		return String.format(MSG_TEMPLATE, getId(), username, jobId, nTime, nonceRight, solution);
	}
	
	/*
	 * {"id":240,"method":"mining.submit","params":["t1VL1tTafDkVnvkiS4R84Mey5WWYKcgMpKf.mainRig1","6b2f458c67ecf48cd64c","b06b4359","de4404000000000000000000000000000000000000000000000000",""]}
	 */
	
}
